def get_data_script_group(dados_script,nome_logon_base,nome_logon_destino):

    dados_script.append('$nomeUsuario1 = "{nome_logon_base}"; $nomeUsuario2 = "{nome_logon_destino}";$listasDistribuicaoUsuario1 = Get-ADPrincipalGroupMembership -Identity $nomeUsuario1 | Where-Object {{ $_.GroupCategory -eq "Distribution" }} | Select-Object -ExpandProperty Name;$listasDistribuicaoUsuario2 = Get-ADPrincipalGroupMembership -Identity $nomeUsuario2 | Where-Object {{ $_.GroupCategory -eq "Distribution" }} | Select-Object -ExpandProperty Name;$listasApenasNoUsuario1 = $listasDistribuicaoUsuario1 | Where-Object {{ $_ -notin $listasDistribuicaoUsuario2 }};$listasAdicionadas = @();foreach ($lista in $listasApenasNoUsuario1) {{ Add-ADGroupMember -Identity $lista -Members $nomeUsuario2; $listasAdicionadas += $lista; }}if ($listasAdicionadas.Count -gt 0) {{ Write-Host "Listas de distribuição adicionadas ao usuário $($nomeUsuario2):"; $listasAdicionadas | ForEach-Object {{ Write-Host $_ }}; }} else {{ Write-Host "Nenhuma lista de distribuição foi adicionada ao usuário $($nomeUsuario2)."; }} \n'.format(nome_logon_base = nome_logon_base, nome_logon_destino = nome_logon_destino))                       
    return dados_script
